.emoji-box:not([style*="transform-origin"]),
.static-popup {
	background-color: var(--elevated-secondary)!important;
	border-radius: var(--radius)!important;
	box-shadow: var(--card-shadow2)!important;
	overflow: hidden;
	border: none!important;
}

.emoji-box:not([style*="transform-origin"]),
.static-popup {
	animation: .4s showInfo;
}

/*去除箭头*/
.emoji-box:before,
.static-popup.bp-arrow:before {
	display: none!important;
}

/*表情*/
.emoji-list .emoji {
	background-color: transparent!important;
}

.emoji-box .emoji-title,
.emoji-box a {
	color: var(--text-primary)!important;
}

.emoji-box a:hover,
.emoji-list .emoji:hover {
	background-color: var(--fill-primary)!important;
}

.emoji-box .emoji-tabs,
.emoji-box .pagination {
	background-color: var(--fill-secondary)!important;
	border-radius: 0!important;
}

.emoji-box .pagination li {
	border-radius: 0!important;
}

/*字符画表情特殊处理*/
.emoji-box .emoji-tabs .tab-link:nth-of-type(2) img,
.emoji-list .emoji.kaomoji span,
.emoji-cover .kaomoji {
	filter: var(--filter-white);
}

/*表情标签列表背景*/
.bp-emoji-box .content-ctnr .pagination {
	background-color: transparent;
	border-radius: 0;
}

/*表情标签列表项*/
.emoji-cover:hover,
.emoji-cover.selected,
.emoji-box .emoji-tabs .tab-link.on,
.emoji-box .pagination li.selected {
	background-color: var(--fill-primary)!important;
}

.emoji-cover {
	border-radius: 0!important;
}

.emoji-tab-slider,
/*左右箭头*/
.page-controller {
	filter: var(--filter-darken);
}

/*勋章详情*/
.bb-comment .nameplate .nameplate-card {
	background-color: var(--elevated-secondary);
	color: var(--text-primary);
	border: none;
	border-radius: var(--radius);
	box-shadow: var(--card-shadow2)!important;
}

.bb-comment .nameplate .nameplate-card .nameplate-arrow {
	display: none;
}

/*勋章详情介绍分割线*/
.bb-comment .nameplate .nameplate-card .nameplate-text,
.comment-bilibili-fold .nameplate .nameplate-card .nameplate-text {
	border: none;
}

/*每一条评论的列表*/
.comment-list .list-item,
.reply-box .reply-item,
.dynamic-list-item-wrap {
	transition: .25s;
}

/*划过每条评论会有亮色遮罩*/
.comment-list .list-item:hover,
.reply-box .reply-item:hover,
.dynamic-list-item-wrap:hover {
	background-color: var(--fill-quaternary);
}

/*评论者用户名*/
.bb-comment .comment-list .list-item .user .name,
.comment-bilibili-fold .comment-list .list-item .user .name {
	color: var(--text-tertiary);
	transition: .25s;
}

/*共有x条回复*/
.bb-comment .comment-list .list-item .reply-box .view-more,
.comment-bilibili-fold .comment-list .list-item .reply-box .view-more {
	color: var(--text-secondary);
}

/*评论信息、楼层、赞、发布时间*/
.comment-list .list-item .info,
.dynamic-list-item-wrap .info {
	color: var(--text-secondary)!important;
}

.comment-list .list-item .user > a:hover,
.comment-list .list-item .user > a.name.vip-red-name:hover {
	color: var(--blue)!important;
}

/*回复*/
.comment-list .list-item .user .text-con {
	color: var(--text-primary)!important;
}

/*评论区回复按钮*/
.bb-comment .comment-list .list-item .info .reply,
.comment-bilibili-fold .comment-list .list-item .info .reply,
.bb-comment .comment-list .list-item .reply-box .view-more .btn-more,
.comment-bilibili-fold .comment-list .list-item .reply-box .view-more .btn-more {
	transition: .25s;
}

/*鼠标划过回复按钮*/
.bb-comment .comment-list .list-item .info .reply:hover,
.comment-bilibili-fold .comment-list .list-item .info .reply:hover,
/*鼠标划过查看更多按钮*/
.bb-comment .comment-list .list-item .reply-box .view-more .btn-more:hover,
.comment-bilibili-fold .comment-list .list-item .reply-box .view-more .btn-more:hover {
	background-color: var(--fill-secondary)!important;
	color: var(--blue);
}

/*去除评论分隔线*/
.comment-list .list-item .con,
/*去除转发动态分割线*/
.dynamic-list-item-wrap {
	border: none!important;
}

.dynamic-list-item-wrap .send-bg {
	background-color: var(--bg-secondary)!important;
}

.bb-comment .hot-line/*热门评论*/
{
	border-color: var(--bg-secondary);
}

.hot-line span {
	color: var(--text-tertiary);
	background-color: var(--bg-primary-solid)!important;
}

.hot-line a {
	transition: .25s;
}

.bb-comment .hot-line a.more-hot:hover {
	background-color: var(--bg-secondary);
}

/*查看更多评论、转发*/
.comment-area .comm-more,
.forw-area .forw-more {
	border-color: var(--bg-secondary)!important
}

.comment-area .comm-more a:not(:hover),
.forw-area .forw-more a:not(:hover) {
	color: var(--text-secondary)!important
}

.card .panel-area {
	margin-bottom: 0!important;
	padding-bottom: 0!important;
}

.panel-area .bb-comment .bottom-page.center {
	border: none;
}

.panel-area .bb-comment .bottom-page.center a {
	display: block;
	padding: 10px;
	margin-bottom: 10px;
	background-color: var(--fill-secondary)!important;
	border-radius: var(--radius);
}

.panel-area .bb-comment .bottom-page.center a:hover {
	background-color: var(--fill-primary)!important;
	color: var(--text-primary)!important;
}

/*bilibili evolved的收起评论按钮*/
.panel-area .bb-comment .fold-comment {
	background-color: var(--bg-primary);
	color: var(--text-tertiary)
}

.panel-area .bb-comment .fold-comment:hover {
	color: var(--theme-color);
}

/**
 **bilibili evolved插件全局调整
 **/
.gui-settings,
.gui-settings-widgets {
	background-color: var(--bg-primary);
}

input[type=text][key] {
