/***************首页****************/
/*洁净首页，嘤语真不好，这样命名凑合吧QwQ*/
/*调整顶栏容器宽度，使内容信息不会太向中间留空*/
.bili-header-m .bili-wrapper.nav-wrapper {
	width: 98%!important;
}

/*调整顶栏阴影，防止过于突兀*/
.bili-header-m .nav-menu .nav-mask {
	box-shadow: none!important;
	border-bottom: 1px solid rgba(120, 120, 128, .2);
}

/*去除所有杂七杂八的，仅保留搜索栏🌚*/
/*这个使顶栏的左侧内容，包含音频区、直播区、商店、漫画下载等等乱七八糟的超链接*/
.nav-con.fl,
/*大会员，，*/
[report-id="big_tab_click"],
/*分区导航栏*/
#primary_menu,
/*接下来是首页大部分内容的容器*/
#app > .bili-wrapper,
/*右侧位置跳转导航条与向上按钮*/
.elevator-module,
.bili-footer {
	display: none!important;
}
/*自定义导航栏布局*/
/*紧凑大搜索栏模式*/
/*去除首页旧模糊背景效果*/
.blur-bg {
	display: none;
}

#banner_link,
.bili-header-m {
	background: transparent!important;
}

.banner-link,
.head-content p.head-title {
	display: none;
}


.head-logo {
	position: absolute !important;
	top: 150px !important;
	margin-left:50px;
	height: 120px!important;
	background-image: var(--logoimg, url(https://i.loli.net/2019/06/11/5cff54794e40241270.png))!important;
	background-position: top;
	background-size: 130px!important;
	filter:drop-shadow(0 4px 6px var(--shadow-dark-color)) var(--filter-blue-turn-pink);
}

@media screen and (max-width:1400px){
	.head-logo{
		margin-left:-10px;
	}
}

/*搜索框*/
.bili-header-m .search {
	top: 150px;
	left: 50%;
	margin-left: -300px;
	padding: 0px;
	width: 600px;
	transition: .3s!important;
	background-color: transparent!important;
	box-shadow: none!important;
}

@media screen and (max-width:1400px){
	.bili-header-m .search{
		margin-left: -285px;
	}
}

.head-content .search .searchform {
	position: relative;
	width: 600px;
	height: 60px!important;
	background-color: transparent!important;
	box-shadow: none!important;
}

.search .search-keyword {
	width: calc(100% - 130px)!important;
	height: 100%!important;
	font-size: 20px !important;
	padding-left: 60px!important;
	padding-right: 70px!important;
	background-color: var(--bg-primary)!important;
	border-radius: var(--radius)!important;
	box-shadow: var(--card-shadow)!important;
	transition: .3s !important;
}

.search .search-keyword:hover {
	background: var(--bg-primary)!important;
	box-shadow: var(--card-shadow2)!important;
}

.search .search-keyword:focus {
	background: var(--bg-primary)!important;
	box-shadow: var(--card-shadow2)!important;
}

/*这是搜索框旁边的放大镜*/
.search .searchform button.search-submit {
	top: -11%;
	margin-top: 20px;
	background-color: transparent;
}

.search .search-keyword + .search-submit {
	transition: right .3s, transform .3s!important;
}

.search .search-keyword + .search-submit:hover {
	transform: scale(1.3) rotate(-10deg) translate(1px, -2px)!important;
}

.search .searchform,
.search .bilibili-suggest {
	transition: .3s !important;
	box-shadow: var(--card-shadow);
	border-radius: var(--radius);
}

.search .bilibili-suggest {
	width: 600px!important;
	left: 0px!important;
}

.bilibili-suggest .suggest-item {
	font-size: 16px;
	padding: 10px 11px!important;
}

.bili-header-m .bilibili-suggest .cancel {
	top: 5px;
}

/*排行榜*/
.search a.link-ranking {
	position: relative;
	top: 8px!important;
	left: 16px!important;
}

.search .link-ranking:hover span {
	transform: scale(1.2) translatey(-.5px)!important
}

.search .link-ranking span {
	width: 0px;
	height: 30px;
	margin-top: 5px;
	overflow: hidden;
}

/*分区导航栏*/
#banner_link + .bili-wrapper {
	margin-top: 150px;
	margin-bottom: 40px!important;
	background-color: transparent!important;
	box-shadow: none;
	transition: .3s;
}

#primary_menu {
	width: 1230px;
	padding: 10px 0;
	border-radius: var(--radius);
	background-color: var(--bg-primary)!important;
	transition: .25s;
	box-shadow: 0 0 0 0 transparent, var(--card-shadow);
	margin-top:150px;
	margin-bottom:40px;
}

#primary_menu:hover {
	box-shadow: 0 0 0 2px var(--content-border-color),
	var(--card-shadow2);
}

/*让右侧导航条自动隐藏*/
.elevator-module:not([style]),
.elevator-module[style="top: 232px;"] {
	opacity: 0;
	top: 20%!important;
}
.elevator-module {
	left: 54%;
	transition: .4s;
}

.elevator-module:hover {
	opacity: 1;
}

.elevator-module .nav-list {
	width: 47px;
	background: var(--elevated-primary);
	border: none;
	border-radius: 10px;
	color: var(--text-primary);
	overflow: visible;
	box-shadow: var(--card-shadow2);
	margin-top: 15px;
}

.elevator-module .nav-list .item.on,
.elevator-module .nav-list .item {
	transition: .25s;
}

.elevator-module .nav-list .item.on,
.elevator-module .nav-list .item:hover:not(.customize) {
	transform: scale(1.2);
	padding-left: 20px;
	margin-left: -20px;
	background-color: var(--theme-color);
	box-shadow: 0 6px 20px var(--theme-light-color);
}

.elevator-module .nav-list .customize {
	border: none;
	height: 30px;
	border-radius: 0 0 10px 10px;
	overflow: hidden;
	color: transparent!important;
}

.elevator-module .nav-list .customize:after {
	position: absolute;
	content: "排序";
	font-size: 12px;
	right: 12px;
	margin-top: 58px;
	color: white;
	transition: .25s;
}

.elevator-module .nav-list .customize {
	position: relative;
}

.elevator-module .nav-list .customize:hover {
	background-color: var(--theme-color);
}

.elevator-module .nav-list .customize i {
	transition: margin .25s;
	position: relative;
	top: 7px;
}

.elevator-module .nav-list .customize:hover i {
	margin-top: -74px;
}

.elevator-module .nav-bg/*排序背景*/
{
	background-color: transparent!important;
	margin-right: 40%;
}

.s-line {
	display: none;
}


.back-top {
	width: 48px;
	margin-top: 20px;
	background-color: var(--elevated-primary)!important;
	border: none!important;
	border-radius: 10px!important;
	box-shadow: var(--card-shadow2);
	transition: background-color .25s, box-shadow .25s, transform .25s;
}

.back-top:hover {
	background-color: var(--theme-color)!important;
	transform: scale(1.1);
	box-shadow: 0 10px 22px var(--theme-light-color);
}

.back-top:active {
	transform: scale(1);
	box-shadow: 0 6px 18px var(--theme-light-color);
}

