	/*用于替换的吧头像（同上）*/

	--m-editortitle: center ;
	/*发贴-标题对齐方式*/



	/*除非你知道自己在做什么，否则不要修改以下变量*/
	--m-baravatarsize: 150px;

	--m-white-color: hsl(0, 0%, var(--m-white-lightness));
	--m-black-color: hsl(0, 0%, var(--m-black-lightness));

	--m-block-b002: hsla(0, 0%, var(--m-black-lightness), calc(0.02*var(--m-block-darken)));
	--m-block-b004: hsla(0, 0%, var(--m-black-lightness), calc(0.04*var(--m-block-darken)));
	--m-block-b006: hsla(0, 0%, var(--m-black-lightness), calc(0.06*var(--m-block-darken)));
	--m-block-b008: hsla(0, 0%, var(--m-black-lightness), calc(0.08*var(--m-block-darken)));
	--m-block-b010: hsla(0, 0%, var(--m-black-lightness), calc(0.1*var(--m-block-darken)));
	--m-block-b020: hsla(0, 0%, var(--m-black-lightness), calc(0.2*var(--m-block-darken)));

	--m-line-b002: hsla(0, 0%, var(--m-black-lightness), calc(0.02*var(--m-line-darken)));
