/*通用提示弹窗*/
.dialogJmodal{
	background-color: #000 !important;
	opacity: calc(0.2*var(--m-block-darken) + 0.2);
	height: 100% !important;
	width: 100% !important;
	position: fixed;
}
.dialogJ{
	padding: 0 !important;
	background: var(--m-white-color) !important;
	border: none !important;
	border-radius: 6px !important;
	overflow: hidden;
	box-shadow: 0 6px 12px rgba(0,0,0,.14);
}
.uiDialogWrapper{
	border: none !important;
	background: none !important;
	color: var(--m-light-40) !important;
}
.dialogJtitle{
	background: var(--m-ecolor) !important;
	color: #fff !important;
	border: none !important;
	height: 36px !important;
	line-height: 36px !important;
}
.dialogJclose{
	position: absolute !important;
	right: 0 !important;
	left: auto !important;
	display: block !important;
	float: right !important;
	height: inherit !important;
	line-height: inherit !important;
	width: 36px !important;
	margin: 0 !important;
	top: 0;
	background: none !important;
	cursor: pointer;
	text-align: center;
	color: rgba(255, 255, 255, .8) !important;
	transform: scale(1);
	transition-property: color;
	transition-duration: .4s;
	transition-timing-function: ease;
}
.dialogJclose:hover {
	color: rgba(255, 255, 255, 1) !important;
}
.dialogJclose:before {
	content: "\e5cd";
	font-family: "Material Icons";
	font-size: 24px;
	display: inline-block;
	-moz-transform: translateY(0.12em);
}
.dialogJcontent{
	background: none !important;
	padding: 12px !important;
}
.dialogJbody{
	height: auto !important;
	padding-bottom: 10px !important;
}
.dialogJanswers{
	padding: 10px 8px 20px !important;
}

