	margin-bottom: 4px;
}

.sketchpad_v_toolbar .color_panel,
.sketchpad_v_toolbar .sketchpad_button_eraser{
	margin-bottom: 10px;
}

.sketchpad_v_toolbar .color_panel{
	display: flex;
	flex-wrap: wrap;
	margin-right: -2px;
}
.sketchpad_v_toolbar .color_panel>li{
	margin: 0 2px 2px 0;
	background: none;
	border: 1px solid var(--m-line-b010);
}
.sketchpad_v_toolbar .color_panel>li>div{
	width: 10px;
	height: 10px;
}

.sketchpad_v_toolbar .sketchpad_button_brush,
.sketchpad_v_toolbar .sketchpad_button_eraser{
	height: 20px;
	background: var(--m-ecolor);
	border-radius: 4px;
	position: relative;
	padding-left: 20px;
}
.sketchpad_button_brush:before,
.sketchpad_button_eraser:before{
	position: absolute;
	top: 0;
	left: 0;
	font-size: 16px;
	line-height: 20px;
	width: 20px;
	text-align: center;
	font-family: "Material Icons";
	color: #fff;
	-moz-transform: translateY(0.125em);
}

.sketchpad_button_brush:before{
	content: "\e3ae";
}
.sketchpad_button_eraser:before{
	content: "\e156";
}

.sketchpad_brush_panel{
	height: 100%;
	display: flex;

}
.sketchpad_v_toolbar .brush_button{
	flex: 1;
	height: 100%;
	margin: 0;
	padding: 0;
	background: none;
	position: relative;
	box-sizing: border-box;
	border: none;
	border-radius: 2px;
	cursor: pointer;
}
.sketchpad_v_toolbar .brush_button:hover{
	background: none;
}
.sketchpad_v_toolbar .brush_button.focus{
	background: rgba(0,0,0,.3);
}
.brush_button:before{
	content: "";
	position: absolute;
	top: 50%;
	left: 50%;
	display: block;
	margin: -2px 0 0 -2px ;
	width: 4px;
	height: 4px;
	border-radius: 50%;
	background: #fff;
}
.brush_middle:before{
	transform: scale(1.5);
}
.brush_big:before{
	transform: scale(2);
}

.sketchpad_bg_input{
	margin: 0;
}

.sketchpad_add_bg,
.sketchpad_remove_bg,
.sketchpad_undo,
.sketchpad_redo,
.sketchpad_clear{
	background: none !important;
	color: #fff !important;
	background: var(--m-ecolor) !important;
	border: none !important;
	border-radius: 4px !important;
	border: 1px solid rgba(0,0,0,0) !important;
	transition-property: background-color,color,border-color,opacity;
	transition-duration: .2s;
	transition-timing-function: ease;
	padding: 0 6px!important;
	width: auto !important;
}
.sketchpad_undo[disabled],
.sketchpad_redo[disabled],
.sketchpad_clear[disabled],
.sketchpad_remove_bg.disabled{
	color: var(--m-clight-20)  !important;
	background: var(--m-light-80)  !important;
	cursor: not-allowed  !important;
	opacity: .5 !important;
}
.sketchpad_undo:before,
.sketchpad_redo:before,
.sketchpad_clear:before,
.sketchpad_finish:before,
.sketchpad_add_bg:before,
.sketchpad_remove_bg:before{
	font-size: 16px;
	font-family: "Material Icons";
	display: inline-block;
	vertical-align: bottom;
	margin-right: 4px;
	-moz-transform: translateY(0.125em);
}
.sketchpad_add_bg:before{
	content: "\e3f4"
}
.sketchpad_remove_bg:before{
	content: "\e3ad"
}
.sketchpad_finish:before{
	content: "\e876"
}
.sketchpad_undo:before{
	content: "\e166"
}
.sketchpad_redo:before{
	content: "\e15a"
}
.sketchpad_clear:before{
	content: "\e53c"
}

